tinymce.init({
  selector: 'textarea',
  images_upload_base_path: '/img/conteudo',
  height: 500,
  plugins: [
    'advlist autolink lists link image charmap print preview anchor',
    'searchreplace visualblocks code fullscreen',
    'insertdatetime media table contextmenu paste code',
    'textcolor',
    'codesample'
  ],
  toolbar: 'insertfile undo redo | styleselect | bold italic | alignleft aligncenter alignright alignjustify | forecolor backcolor | bullist numlist outdent indent | link image | codesample',
});

$(document).ready(function(){
    $('#con_data').mask("99/99/9999");
});

$(function() {
    $("#con_data").datepicker({dateFormat: 'dd/mm/yy'});
});

$("#con_imagem").fileinput({
  allowedFileExtensions: ['jpg','jpeg', 'png', 'gif'],
  maxFileSize: 25000,
  showPreview: false,
  showUpload: false,
  showRemove: true,
  msgValidationError: 'Arquivo n√£o suportado...',
  maxFileCount: 10,
  language: 'pt-BR'
});



$(document).ready(function() {
        $('#sub_areas').multiselect({
            includeSelectAllOption: true,
            enableCollapsibleOptGroups: true,
            buttonWidth: '100%',
            enableFiltering: true,
            numberDisplayed: 2
        });
    });
    
$("[name='con_destaque']").bootstrapSwitch({size:'small'});